            <a href="/contact" className="btn-primary" data-testid="link-start-project-cta">
              Start Your Project
            </a>
            <a href="/case-studies" className="btn-secondary" data-testid="link-see-results-cta">
              See Results
            </a>
          </div>
        </div>
      </section>
    </div>
  );
}

