                  alt={`${title} logo`} 
                  className="max-h-16 max-w-32 object-contain" 
                />
              ) : (
                <div className="h-16 w-16 bg-primary/10 rounded-lg flex items-center justify-center">
                  <span className="text-primary font-bold text-xl">{title.charAt(0)}</span>
                </div>
              )}
